import React from "react";

import "./loader.scss";

//lazyload prop option
const Loader = (props) => {
  const loader = document.querySelector(".loader");

  const show = () => {
    loader.classList.remove("loader-hide");
  };

  //lazyloading
  const lazyload = () => {
    //after 2 seconds opacity will go to 0
    //after that 2.5 seconds the loader will be removed from the dom to allow the user to access the site
    setTimeout(() => {
      loader.style.opacity = "0";
      setTimeout(() => {
        loader.classList.add("loader-hide");
      }, 2500);
    }, 2000);
  };

  //if lazyload is true the loader will become a lazyloader
  if (props.lazyload === true) {
    if (loader) {
      lazyload();
    }
  }

  if (props.show === true) {
    show();
  }

  return (
    <div className="loader">
      <div className="lds-ripple">
        <div></div>
        <div></div>
      </div>
    </div>
  );
};

export default Loader;
