class Carousel extends React.Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 3,
      slidesToScroll: 3,
      className: "slides",
    };

    return (
      <div>
        <Slider {...settings}>
          {photos.map(photo => {
            return (
              <div key={photo.name}>
                <img src={photo.url} alt={photo.name} />
              </div>
            );
          })}
        </Slider>
      </div>
    );
  }
}

export default Carousel;
