import React from "react";
import Carousel from "@brainhubeu/react-carousel";
import "@brainhubeu/react-carousel/lib/style.css";
import { Link } from "react-router-dom";

import "./imageCarousel.scss";

class MovieCarousel extends React.Component {
  mapMovies() {
    if (this.props.movies) {
      //loops through movies foreach movie returns jsx
      return this.props.movies.map(movie => {
        return (
          <div key={movie.id} className="img-slide">
            <Link to={`/moviedetails/${movie.id}`}>
              <div
                className="img-slide-image"
                style={{
                  background: `linear-gradient(0deg, rgb(0, 0, 0, 0.45) 5%, rgba(0, 0, 0, 0.45) 5%), url(http://image.tmdb.org/t/p/original/${movie.backdrop_path}) center/cover no-repeat border-box, rgb(255, 255, 255)`,
                }}
              ></div>
            </Link>
          </div>
        );
      });
    }
  }

  render() {
    const settings = {
      slidesPerPage: 1,
      slidesPerScroll: 1,
      infinite: true,
      animationSpeed: 500,
      arrows: false,
    };
    return <Carousel {...settings}>{this.mapMovies()}</Carousel>;
  }
}

export default MovieCarousel;
