import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { connect } from "react-redux";

import "./carousel.scss";

class CustomSlide extends React.Component {
  renderImage = poster_path => {
    if (poster_path) {
      return `http://image.tmdb.org/t/p/w185/${poster_path}`;
    }
    return "https://in.bmscdn.com/iedb/movies/images/website/poster/large/ela-cheppanu-et00016781-24-03-2017-18-31-40.jpg";
  };
  render() {
    const { index, ...props } = this.props;

    return this.props.movies.map(movie => {
      return (
        <div key={movie.id} {...props}>
          <img src={this.renderImage(movie.poster_path)} alt={movie.title} />
          <p>{movie.title}</p>
        </div>
      );
    });
  }
}

const mapStateToProps = state => {
  return { movies: state.movieData.results };
};

connect(mapStateToProps)(CustomSlide);

export default class Responsive extends React.Component {
  render() {
    var settings = {
      dots: true,
      arrows: true,
      infinite: false,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0,
      className: "slides",
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true,
            arrows: true,
          },
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2,
          },
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
            arrows: false,
          },
        },
      ],
    };
    return (
      <div>
        <h2> Responsive </h2>
        <Slider {...settings}>
          <CustomSlide index={1} />
        </Slider>
      </div>
    );
  }
}
