import {
  FETCH_TV_LATEST,
  FETCH_TV_AIRING,
  FETCH_TV_POPULAR,
  FETCH_TV_TOP_RATED,
} from "../actions/types";

export default (state = {}, { type, payload }) => {
  switch (type) {
    case FETCH_TV_LATEST:
      return { ...state, latest: payload };
    case FETCH_TV_AIRING:
      return { ...state, airing: payload };
    case FETCH_TV_POPULAR:
      return { ...state, popular: payload };
    case FETCH_TV_TOP_RATED:
      return { ...state, topRated: payload };
    default:
      return state;
  }
};
