import React from "react";
import { connect } from "react-redux";

class UserHeader extends React.Component {
  render() {
    const { user } = this.props; //find is a build in method for arrays

    if (!user) {
      return null;
    }

    return <div className="header">{user.name}</div>;
  }
}

//Do all logic here rather than on the component
//This has access to props from the component
const mapStateToProps = (state, ownProps) => {
  return { user: state.users.find(user => user.id === ownProps.userId) }; //ownProps is props
};

export default connect(mapStateToProps)(UserHeader);
