import React from "react";
import Swiper from "react-id-swiper";
import "swiper/css/swiper.css";

import MovieCard from "../MovieCard";

const Carousel = () => {
  const params = {
    effect: "cube",
    grabCursor: true,
    cubeEffect: {
      shadow: true,
      slideShadows: true,
      shadowOffset: 20,
      shadowScale: 0.94,
    },
    pagination: {
      el: ".swiper-pagination",
    },
  };
  return (
    <Swiper {...params}>
      <Swiper-Slide>
        <MovieCard />
      </Swiper-Slide>
    </Swiper>
  );
};
export default Carousel;
