import React from "react";

//items prop for list of items
const ItemCard = props => {
  const renderImage = imagePath => {
    if (imagePath) {
      return `http://image.tmdb.org/t/p/w185/${imagePath}`;
    }
    return "https://in.bmscdn.com/iedb/movies/images/website/poster/large/ela-cheppanu-et00016781-24-03-2017-18-31-40.jpg";
  };

  if (this.props.movies) {
    return this.props.movies.map(movie => {
      return (
        <div key={movie.id}>
          <img src={renderImage(movie.poster_path)} alt={movie.title} />
          <p>{movie.title}</p>
        </div>
      );
    });
  }
  return "Search Movies";
};

export default ItemCard;
