import React from "react";

import { connect } from "react-redux";
import { fetchMovies } from "../actions";

class MovieList extends React.Component {
  componentDidMount() {
    this.props.fetchMovies("spider man"); //use action creator
  }

  /*
  //used for logic to render in render()
  //Having multiple posts auto generated
  renderList() {
    return this.props.movies.map(movie => {
      return <div>{movie}</div>;
    });
  }
*/
  render() {
    console.log(this.props.movies);
    return <div>{this.props.title}</div>; //render posts
  }
}

const mapStateToProps = state => {
  return { movies: state, results: state.total_results };
};

export default connect(mapStateToProps, { fetchMovies })(MovieList);
