import React from "react";
import Swiper from "swiper";
import "swiper/css/swiper.css";
import { connect } from "react-redux";

import "./carousel.scss";
import { fetchTrending } from "../../actions";

class Carousel extends React.Component {
  componentDidMount() {
    this.props.fetchTrending();
  }
  mapTrending() {
    if (this.props.trending) {
      return this.props.trending.map(movie => {
        return (
          <div key={movie.id}>
            <img
              src={`http://image.tmdb.org/t/p/w185/${movie.poster_path}`}
              alt={movie.title}
            />
            <p>{movie.title}</p>
          </div>
        );
      });
    }
  }

  //initialize new instance of swiper when component mounts

  render() {
    new Swiper(".swiper-container", {
      slidesPerView: 3,
      spaceBetween: 10,

      loop: true,
      pagination: {
        el: ".swiper-pagination",
        clickable: true,
      },
      navigation: {
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev",
      },
      /*observer: true,
        observeParents: true,*/
    });
    return (
      <div className="swiper-container">
        <div className="swiper-wrapper">{this.mapTrending()}</div>

        <div className="swiper-pagination"></div>

        <div className="swiper-button-next"></div>
        <div className="swiper-button-prev"></div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { trending: state.trendingData.results };
};

export default connect(mapStateToProps, { fetchTrending })(Carousel);
