import React from "react";

//items prop for list of items
const ItemCard = props => {
  //props
  //ImgbaseUrl
  //imagePath
  //defaultImage
  const renderImage = imagePath => {
    if (imagePath) {
      return `${props.baseUrl}${imagePath}`;
    }
    return `${props.defaultImage}`;
  };

  //props
  //items
  if (props.items) {
    return props.items.map(item => {
      return (
        <React.Fragment key={item.id}>
          <img src={renderImage(item.imagePath)} alt={item.title} />
          <p>{item.title}</p>
        </React.Fragment>
      );
    });
  }
  return "Search Movies";
};

export default ItemCard;
