import React from "react";

import "./loader.scss";

//lazyload prop option
const Loader = (props) => {
  const lazyload = () => {};

  //if lazyload is true the loader will become a lazyloader
  if (props.lazyload === true) {
    lazyload();
  }
  return (
    <div className="loader">
      <div className="lds-ripple">
        <div></div>
        <div></div>
      </div>
    </div>
  );
};

export default Loader;
