import { FETCH_TV_POPULAR, FETCH_TV_TOP_RATED } from "../../actions/types";
export default (state = [], { type, payload }) => {
  switch (type) {
    case FETCH_TV_POPULAR:
      return { popular: payload, ...state };
    default:
      return state;
  }
};
