import React from "react";

//items prop for list of items
const ItemCard = props => {
  //props
  //imgBaseUrl
  //defaultImage
  const renderImage = imagePath => {
    if (imagePath) {
      return `${props.imgBaseUrl}${imagePath}`;
    }
    return `${props.defaultImage}`;
  };

  //props
  //items
  if (props.items) {
    return props.items.map(item => {
      return (
        <React.Fragment>
          {" "}
          <div key={item.id}>
            <img src={renderImage(item.poster_path)} alt={item.title} />
            <p>{item.title}</p>
          </div>
        </React.Fragment>
      );
    });
  }
  return "Search Movies";
};

export default ItemCard;
