import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { connect } from "react-redux";

import "./carousel.scss";
import { fetchTrending } from "../../actions";

class Carousel extends React.Component {
  componentDidMount() {
    this.props.fetchTrending();
  }
  mapTrending() {
    if (this.props.trending) {
      this.props.trending.results.map(movie => {
        return <div>{movie.title}</div>;
      });
    }
  }
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 3,
      slidesToScroll: 3,
      className: "slides",
    };
    /*
    {photos.map(photo => {
        return (
          <div key={photo.name}>
            <img src={photo.url} alt={photo.name} />
          </div>
        );
      })}*/
    return (
      <div>
        <Slider {...settings}>{this.mapTrending()}</Slider>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { trending: state.trendingData.results };
};

export default connect(mapStateToProps, { fetchTrending })(Carousel);
