import React from "react";
import Swiper from "react-id-swiper";
import { connect } from "react-redux";

import "swiper/css/swiper.css";

const Carousel = props => {
  const params = {
    slidesPerView: 30,
    spaceBetween: 30,
    pagination: {
      el: ".swiper-pagination",
      clickable: true,
    },
  };
  const renderImage = poster_path => {
    if (poster_path) {
      return `http://image.tmdb.org/t/p/w185/${poster_path}`;
    }
    return "https://in.bmscdn.com/iedb/movies/images/website/poster/large/ela-cheppanu-et00016781-24-03-2017-18-31-40.jpg";
  };
  const renderItems = () => {
    if (props.movies) {
      return props.movies.map(movie => {
        return (
          <div key={movie.id}>
            <img src={renderImage(movie.poster_path)} alt={movie.title} />
            <p>{movie.title}</p>
          </div>
        );
      });
    }
    return " ";
  };

  return <Swiper {...params}>{renderItems()}</Swiper>;
};

const mapStateToProps = state => {
  return { movies: state.movieData.results };
};

export default connect(mapStateToProps)(Carousel);
